import { createReducer, on } from '@ngrx/store';
import { getUser, setUser, unsetUser } from './auth.actions';
export const usuarioLogueadoStateInicial = {
  user: null
};
export const authReducer = createReducer(usuarioLogueadoStateInicial, on(setUser, (state, {
  user
}) => {
  return Object.assign(Object.assign({}, state), {
    user: Object.assign({}, user)
  });
}), on(unsetUser, state => {
  return Object.assign(Object.assign({}, state), {
    user: null
  });
}), on(getUser, state => {
  return Object.assign({}, state);
}));
